import math

def main():
    p = int('', 16)
    q = int('', 16)
    n = int( '', 16)
    d = int( '', 16)
    e = 65537
    lamN = lcm(p-1, q-1)
    if(p * q == n):
        print("p * q does equal n!")
    print("d * e mod lambda(n):", (d * e) % lamN)

def lcm(x, y):
    return x * y // math.gcd(x,y)

if __name__ == "__main__":
    main()
